import { createStore } from 'vuex'

export default createStore({
  state: {
    userIsAuthorized:true
  },
  getters: {
  },
  mutations: {
    setUserIsAuthenticated(state, replacement){
      state.userIsAuthorized = replacement;
    }
  },
  actions: {
    auth0Login(context){
      context.state.auth0.authorize()
    }
  },
  modules: {
  }
})
